mapper.delete:

DELETE FROM COMTNFILEDETAIL
WHERE
	ATCH_FILE_ID = #{atchFileId}
AND	
	FILE_SN = #{fileSn}

mapper.select:

SELECT NVL(MAX(FILE_SN),0)+1 AS FILE_SN
FROM COMTNFILEDETAIL
WHERE ATCH_FILE_ID =  #{atchFileId}

mapper.update:

UPDATE COMTNFILE
SET USE_AT = 'N'
WHERE ATCH_FILE_ID = #{atchFileId}
