mapper.select:

SELECT NVL(MAX(RDCNT),0)+1 AS RDCNT FROM COMTNBBS
WHERE BBS_ID = #{bbsId}
AND NTT_ID = #{nttId}

mapper.update:

UPDATE COMTNBBS SET 
	RDCNT = #{inqireCo},
	LAST_UPDUSR_ID = #{lastUpdusrId},
	LAST_UPDT_PNTTM = SYSDATE
WHERE BBS_ID = #{bbsId}
AND NTT_ID = #{nttId}

mapper.select:
SELECT * FROM ( SELECT rownum rn, TB.* FROM ( SELECT a.NTT_ID, a.NTT_SJ, a.NTT_CN, a.FRST_REGISTER_ID, NVL(b.USER_NM, a.NTCR_NM) as FRST_REGISTER_NM, TO_CHAR(a.FRST_REGIST_PNTTM, 'YYYY-MM-DD') as FRST_REGIST_PNTTM, a.RDCNT, a.PARNTSCTT_NO, a.ANSWER_AT, a.ANSWER_LC, a.USE_AT, a.ATCH_FILE_ID, a.BBS_ID, a.NTCE_BGNDE, a.NTCE_ENDDE, a.SJ_BOLD_AT, a.NOTICE_AT, a.SECRET_AT, c.COMMENT_CO, a.NTCR_ID, d.BLOG_ID, d.REPLY_POSBL_AT FROM COMTNBBS a LEFT OUTER JOIN COMVNUSERMASTER b ON a.FRST_REGISTER_ID = b.ESNTL_ID LEFT OUTER JOIN (SELECT NTT_ID, BBS_ID, COUNT(1) AS COMMENT_CO FROM COMTNCOMMENT WHERE USE_AT = 'Y' GROUP BY NTT_ID, BBS_ID) c ON a.NTT_ID = c.NTT_ID LEFT OUTER JOIN COMTNBBSMASTER d ON a.BBS_ID = d.BBS_ID WHERE a.BBS_ID = #{bbsId} AND a.USE_AT = 'Y' ORDER BY a.SORT_ORDR DESC, NTT_NO ASC
) TB ) WHERE rn BETWEEN 1 AND 5
) TB ) WHERE rn BETWEEN 1 AND 10
) TB ) WHERE rn BETWEEN 1 AND 15
) TB ) WHERE rn BETWEEN 1 AND 20
) TB ) WHERE rn BETWEEN 1 AND 30
