mapper.select:

SELECT MNGR_AT, USE_AT FROM
COMTNCMMNTYUSER
WHERE EMPLYR_ID = #{emplyrId} 
	AND CMMNTY_ID =  #{cmmntyId}


SELECT COUNT(CMMNTY_ID)
	FROM
COMTNCMMNTYUSER
	WHERE EMPLYR_ID = #{emplyrId} 
		AND CMMNTY_ID = #{cmmntyId}

SELECT * FROM ( SELECT rownum rn, TB.* FROM ( SELECT a.CMMNTY_ID , a.EMPLYR_ID , b.USER_NM AS EMPLYR_NM , b.USER_ID , b.USER_EMAIL , a.MNGR_AT , a.SBSCRB_DE , a.SECSN_DE , a.MBER_STTUS , c.CODE_NM AS MBER_STTUS_NM , a.USE_AT , a.FRST_REGIST_PNTTM , a.FRST_REGISTER_ID FROM COMTNCMMNTYUSER a LEFT OUTER JOIN COMVNUSERMASTER b ON a.EMPLYR_ID = b.ESNTL_ID LEFT OUTER JOIN (SELECT CODE_ID, CODE, CODE_NM FROM COMTCCMMNDETAILCODE WHERE CODE_ID = 'COM013' AND USE_AT='Y') c ON a.MBER_STTUS = c.CODE WHERE 1=1 AND a.CMMNTY_ID = #{cmmntyId}
AND b.USER_NM LIKE '%' || #{searchWrd} || '%'
ORDER BY b.USER_NM ) TB ) WHERE rn BETWEEN #{firstIndex} + 1 AND #{firstIndex} + #{recordCountPerPage}

mapper.update:

UPDATE COMTNCMMNTYUSER
	SET MBER_STTUS = 'P'
		, LAST_UPDT_PNTTM = SYSDATE
		, LAST_UPDUSR_ID = #{lastUpdusrId}
WHERE CMMNTY_ID = #{cmmntyId}
	AND EMPLYR_ID = #{emplyrId}

mapper.delete:

DELETE FROM COMTNCMMNTYUSER
	WHERE CMMNTY_ID = #{cmmntyId}
		AND EMPLYR_ID = #{emplyrId}

mapper.update:

UPDATE COMTNCMMNTYUSER
	SET MNGR_AT = 'Y'
		, LAST_UPDT_PNTTM = SYSDATE
		, LAST_UPDUSR_ID = #{lastUpdusrId}
WHERE CMMNTY_ID = #{cmmntyId}
	AND EMPLYR_ID = #{emplyrId}


UPDATE COMTNCMMNTYUSER
	SET MNGR_AT = 'N'
		, LAST_UPDT_PNTTM = SYSDATE
		, LAST_UPDUSR_ID = #{lastUpdusrId}
WHERE CMMNTY_ID = #{cmmntyId}
	AND EMPLYR_ID = #{emplyrId}
