mapper.delete:
DELETE FROM COMTNNCRDUSER
WHERE NCRD_ID = #{ncrdId} 
AND EMPLYR_ID = #{emplyrId}

mapper.select:
SELECT * FROM ( SELECT rownum rn, TB.* FROM ( SELECT NCRD_ID, NM, NCRD_TRGTER_ID, OTHBC_AT, EXTRL_USER_AT, OTHBC_AT CMPNY_NM, DEPT_NM, IDNTFC_NO, MIDDLE_MBTLNUM, END_MBTLNUM, FRST_REGIST_PNTTM, FRST_REGISTER_ID FROM COMTNNCRD WHERE FRST_REGISTER_ID = #{frstRegisterId}
AND NM LIKE '%' || #{searchWrd} || '%'
AND CMPNY_NM LIKE '%' || #{searchWrd} || '%'
AND DEPT_NM LIKE '%' || #{searchWrd} || '%'
ORDER BY NM ) TB ) WHERE rn BETWEEN #{firstIndex} + 1 AND #{firstIndex} + #{recordCountPerPage}

SELECT * FROM ( SELECT rownum rn, TB.* FROM ( SELECT a.NCRD_ID, b.NM, b.CMPNY_NM, b.DEPT_NM, b.FRST_REGIST_PNTTM, a.EMPLYR_ID, a.USE_AT, c.USER_NM, a.CREAT_DT, b.FRST_REGISTER_ID FROM COMTNNCRDUSER a LEFT OUTER JOIN COMTNNCRD b ON a.NCRD_ID = b.NCRD_ID LEFT OUTER JOIN COMVNUSERMASTER c ON a.EMPLYR_ID = c.ESNTL_ID WHERE a.USE_AT = 'Y' AND a.EMPLYR_ID = #{emplyrId}
AND NM LIKE '%' || #{searchWrd} || '%'
AND CMPNY_NM LIKE '%' || #{searchWrd} || '%'
AND DEPT_NM LIKE '%' || #{searchWrd} || '%'
ORDER BY NM ) TB ) WHERE rn BETWEEN #{firstIndex} + 1 AND #{firstIndex} + #{recordCountPerPage}
