mapper.select:
SELECT * FROM ( SELECT T0.*, ROWNUM RNUM FROM (
SELECT b.ORGNZT_NM, a.ESNTL_ID, a.USER_NM, a.EMPL_NO, a.OFCPS_NM FROM COMTNEMPLYRINFO a LEFT OUTER JOIN COMTNORGNZTINFO b ON a.ORGNZT_ID = b.ORGNZT_ID WHERE 1=1
AND NVL(b.ORGNZT_NM, ' ') LIKE '%'||#{searchWrd}||'%'
AND NVL(a.USER_NM, ' ') LIKE '%'||#{searchWrd}||'%'
ORDER BY b.ORGNZT_NM, a.USER_NM ASC
) T0 ) Z WHERE RNUM BETWEEN #{firstIndex} + 1 AND #{firstIndex} + #{recordCountPerPage}

SELECT * FROM ( SELECT T0.*, ROWNUM RNUM FROM (
SELECT ORGNZT_ID, ORGNZT_NM, ORGNZT_DC FROM COMTNORGNZTINFO WHERE 1=1
AND ORGNZT_NM LIKE '%'||#{searchWrd}||'%'
AND ORGNZT_DC LIKE '%'||#{searchWrd}||'%'
ORDER BY ORGNZT_NM ASC
) T0 ) Z WHERE RNUM BETWEEN #{firstIndex} + 1 AND #{firstIndex} + #{recordCountPerPage}

SELECT * FROM ( SELECT T0.*, ROWNUM RNUM FROM (
SELECT A.DEPT_ID, B.ORGNZT_NM DEPT_NM, A.DEPT_JOBBX_ID, A.DEPT_JOBBX_NM, A.INDICT_ORDR, C.USER_NM LAST_UPDUSR_NM, A.LAST_UPDT_PNTTM FROM COMTNDEPTJOBBX A LEFT OUTER JOIN COMTNORGNZTINFO B ON A.DEPT_ID = B.ORGNZT_ID LEFT OUTER JOIN COMTNEMPLYRINFO C ON A.LAST_UPDUSR_ID = C.ESNTL_ID WHERE 1=1
AND A.DEPT_ID = #{deptId}
AND NVL(B.ORGNZT_NM, ' ') LIKE '%'||#{searchWrd}||'%'
AND A.DEPT_JOBBX_NM LIKE '%'||#{searchWrd}||'%'
ORDER BY B.ORGNZT_NM, A.INDICT_ORDR ASC
) T0 ) Z WHERE RNUM BETWEEN #{firstIndex} + 1 AND #{firstIndex} + #{recordCountPerPage}


	SELECT
		COUNT(DEPT_JOBBX_ID) as cnt
	FROM
		COMTNDEPTJOBBX 

WHERE DEPT_ID=#{deptId}


		 AND
		 	 INDICT_ORDR <= #{indictOrdr} 


		 AND
			 INDICT_ORDR >= #{indictOrdr} 



		AND INDICT_ORDR = #{indictOrdr} 

	 AND DEPT_JOBBX_ID != #{deptJobBxId}

mapper.update:

UPDATE COMTNDEPTJOBBX 
SET  
	INDICT_ORDR = INDICT_ORDR + 1
WHERE DEPT_ID=#{deptId}
	AND INDICT_ORDR = #{indictOrdr}


UPDATE COMTNDEPTJOBBX 
SET  
	INDICT_ORDR = INDICT_ORDR - 1
WHERE DEPT_ID=#{deptId}
	AND INDICT_ORDR = #{indictOrdr}


UPDATE COMTNDEPTJOBBX 
SET  
	INDICT_ORDR = #{indictOrdr}
WHERE DEPT_JOBBX_ID = #{deptJobBxId}


UPDATE COMTNDEPTJOBBX 
SET  
	INDICT_ORDR = INDICT_ORDR + 1
WHERE DEPT_ID=#{deptId}
		AND INDICT_ORDR >= #{indictOrdr}

mapper.select:
SELECT
	COUNT(DEPT_JOBBX_ID) as cnt
FROM
	COMTNDEPTJOBBX 
WHERE DEPT_ID = #{deptId}
	AND DEPT_JOBBX_NM = #{deptJobBxNm}

SELECT * FROM ( SELECT T0.*, ROWNUM RNUM FROM (
SELECT B.DEPT_ID, A.DEPT_JOB_ID, A.DEPT_JOB_NM, A.PRIORT, C.USER_NM CHARGER_NM, A.FRST_REGIST_PNTTM FROM COMTNDEPTJOB A LEFT OUTER JOIN COMTNDEPTJOBBX B ON A.DEPT_JOBBX_ID = B.DEPT_JOBBX_ID LEFT OUTER JOIN COMTNEMPLYRINFO C ON A.CHARGER_ID = C.ESNTL_ID
WHERE A.DEPT_JOBBX_ID = #{searchDeptJobBxId}
WHERE B.DEPT_ID = #{searchDeptId}
AND A.DEPT_JOB_NM LIKE '%'||#{searchWrd}||'%'
AND A.DEPT_JOB_CN LIKE '%'||#{searchWrd}||'%'
AND C.USER_NM LIKE '%'||#{searchWrd}||'%'
ORDER BY A.FRST_REGIST_PNTTM DESC
) T0 ) Z WHERE RNUM BETWEEN #{firstIndex} + 1 AND #{firstIndex} + #{recordCountPerPage}

SELECT COUNT(A.DEPT_JOB_ID) as cnt FROM COMTNDEPTJOB A LEFT OUTER JOIN COMTNDEPTJOBBX B ON A.DEPT_JOBBX_ID = B.DEPT_JOBBX_ID LEFT OUTER JOIN COMTNEMPLYRINFO C ON A.CHARGER_ID = C.ESNTL_ID
WHERE A.DEPT_JOBBX_ID = #{searchDeptJobBxId}
WHERE B.DEPT_ID = #{searchDeptId}
AND A.DEPT_JOB_NM LIKE '%'||#{searchWrd}||'%'
AND A.DEPT_JOB_CN LIKE '%'||#{searchWrd}||'%'
AND C.USER_NM LIKE '%'||#{searchWrd}||'%'
