mapper.select:
SELECT A.TODO_ID, A.TODO_SJ, A.TODO_BEGIN_TIME, A.TODO_END_TIME, IFNULL(B.USER_NM, C.MBER_NM) WRTER_NM, A.FRST_REGIST_PNTTM FROM COMTNMEMOTODO A LEFT OUTER JOIN COMTNEMPLYRINFO B ON A.WRTER_ID = B.ESNTL_ID LEFT OUTER JOIN COMTNGNRLMBER C ON A.WRTER_ID = C.ESNTL_ID WHERE A.WRTER_ID = #{searchId}
AND A.FRST_REGIST_PNTTM BETWEEN #{searchBgnDe} AND #{searchEndDe}
AND (SUBSTR(A.TODO_BEGIN_TIME, 1, 10) BETWEEN #{searchBgnDe} AND #{searchEndDe} || SUBSTR(A.TODO_END_TIME, 1, 10) BETWEEN #{searchBgnDe} AND #{searchEndDe} )
AND binary(A.TODO_SJ) LIKE CONCAT('%', #{searchWrd}, '%')
AND binary(A.TODO_CN) LIKE CONCAT('%', #{searchWrd}, '%')
ORDER BY A.TODO_BEGIN_TIME DESC LIMIT #{recordCountPerPage} OFFSET #{firstIndex}

SELECT COUNT(A.TODO_ID) as cnt FROM COMTNMEMOTODO A WHERE A.WRTER_ID = #{searchId}
AND A.FRST_REGIST_PNTTM BETWEEN #{searchBgnDe} AND #{searchEndDe}
AND (SUBSTR(A.TODO_BEGIN_TIME, 1, 10) BETWEEN #{searchBgnDe} AND #{searchEndDe} || SUBSTR(A.TODO_END_TIME, 1, 10) BETWEEN #{searchBgnDe} AND #{searchEndDe} )
AND binary(A.TODO_SJ) LIKE CONCAT('%', #{searchWrd}, '%')
AND binary(A.TODO_CN) LIKE CONCAT('%', #{searchWrd}, '%')
