mapper.select:
SELECT * FROM ( SELECT T0.*, ROWNUM RNUM FROM (
SELECT A.KNWLDG_ID KNO_ID , A.KNWLDG_NM KNO_NM , B.KNWLDG_TY_NM KNO_TYPE_NM , D.USER_NM USER_NM , A.KNWLDG_EVL KNO_APS , TO_CHAR(TO_DATE(A.EVL_DE, 'YYYYmmdd'), 'YYYY-mm-dd') AS APP_YMD , A.FRST_REGISTER_ID FRST_REGISTER_ID , A.FRST_REGIST_PNTTM FRST_REGISTER_PNTTM , A.LAST_UPDUSR_ID LAST_UPDUSR_ID , A.LAST_UPDT_PNTTM LAST_UPDUSR_PNTTM FROM COMTNDAMKNOIFM A, COMTNDAMMAPKNO B, COMTNDAMMAPTEAM C, COMVNUSERMASTER D, COMTNDAMPRO E WHERE C.ORGNZT_ID = B.ORGNZT_ID AND A.KNWLDG_TY_CODE = B.KNWLDG_TY_CODE AND A.KNWLDG_TY_CODE = E.KNWLDG_TY_CODE AND E.EXPERT_ID = D.ESNTL_ID AND A.OTHBC_AT = 'Y' AND E.EXPERT_ID = #{emplyrId} AND NVL(A.KNWLDG_EVL,0) NOT IN ('3')
AND A.KNWLDG_NM LIKE '%'||#{searchKeyword}||'%'
AND D.USER_NM LIKE '%'||#{searchKeyword}||'%'
) T0 ) WHERE RNUM BETWEEN #{firstIndex} + 1 AND #{firstIndex} + #{recordCountPerPage}

mapper.update:

            UPDATE  COMTNDAMKNOIFM 
               SET  EVL_DE			  = #{appYmd}
		         ,  KNWLDG_EVL        = #{knoAps}
                 ,  EXPERT_ID    	  = #{speId}
                 ,  LAST_UPDUSR_ID = #{lastUpdusrId}
                 ,  LAST_UPDT_PNTTM = sysdate
             WHERE  OTHBC_AT 		  = 'Y'	
             AND    KNWLDG_ID         = #{knoId}
