mapper.select:
SELECT * FROM ( SELECT T0.*, ROWNUM RNUM FROM (
SELECT ORGNZT_ID , ORGNZT_NM , CL_DE , KNWLDG_URL FROM COMTNDAMMAPTEAM WHERE 1 = 1
AND ORGNZT_NM LIKE '%'||#{searchKeyword}||'%'
AND ORGNZT_ID LIKE '%'||#{searchKeyword}||'%'
) T0 ) WHERE RNUM BETWEEN #{firstIndex} + 1 AND #{firstIndex} + #{recordCountPerPage}

mapper.insert:

INSERT 
  INTO  COMTNDAMMAPTEAM
     (  ORGNZT_ID
     ,  ORGNZT_NM
     ,  CL_DE
     ,  KNWLDG_URL
     ,  LAST_UPDT_PNTTM
     ,  LAST_UPDUSR_ID
     ) 
VALUES  
     (  #{orgnztId}
     ,  #{orgnztNm}
     ,  #{clYmd}
     ,  #{knoUrl}
     ,  sysdate
     ,  #{frstRegisterId}
     )

mapper.update:

UPDATE  COMTNDAMMAPTEAM 
   SET  ORGNZT_ID         = #{orgnztId}
     ,  ORGNZT_NM         = #{orgnztNm}
     ,  CL_DE  		  = #{clYmd}
     ,  KNWLDG_URL           = #{knoUrl}
     ,  LAST_UPDT_PNTTM = sysdate
     ,  LAST_UPDUSR_ID    = #{lastUpdusrId}
 WHERE  ORGNZT_ID         = #{orgnztId}
