mapper.select:
SELECT * FROM ( SELECT T0.*, ROWNUM RNUM FROM (
SELECT A.KNWLDG_ID KNO_ID, A.ORGNZT_ID ORGNZT_ID, (SELECT ORGNZT_NM FROM COMTNDAMMAPTEAM WHERE ORGNZT_ID=A.ORGNZT_ID) ORGNZT_NM, (SELECT KNWLDG_TY_NM FROM COMTNDAMMAPKNO WHERE KNWLDG_TY_CODE=A.KNWLDG_TY_CODE) KNO_TYPE_NM, A.EXPERT_ID SPE_UNIQ, (SELECT USER_NM FROM COMTNEMPLYRINFO WHERE ESNTL_ID = A.EXPERT_ID) SPE_NM, (SELECT USER_NM FROM COMTNEMPLYRINFO WHERE ESNTL_ID = A.EXPERT_ID) SPE_ID, A.KNWLDG_TY_CODE KNO_TYPE_CD, A.EMPLYR_ID EMPLYR_UNIQ, (SELECT USER_NM FROM COMTNEMPLYRINFO WHERE ESNTL_ID = A.EMPLYR_ID) USER_NM, (SELECT USER_NM FROM COMTNEMPLYRINFO WHERE ESNTL_ID = A.EMPLYR_ID) EMPLYR_ID, A.KNWLDG_NM KNO_NM, A.KNWLDG_CN KNO_CN, A.ATCH_FILE_ID ATCH_FILE_ID, A.PARNTS_KNWLDG_ID ANS_PARENTS, A.ANSWER_DP ANS_DEPTH, A.ANSWER_ORDR ANS_SEQ, A.ANSWER_GROUP_NO ANS_NUMBER, A.FRST_REGISTER_ID FRST_REGISTER_ID, TO_CHAR(A.FRST_REGIST_PNTTM,'YYYY-MM-DD') FRST_REGIST_PNTTM, (SELECT USER_NM FROM COMTNEMPLYRINFO WHERE ESNTL_ID = A.FRST_REGISTER_ID) FRST_REGISTER_NM FROM COMTNDAMCALRES A WHERE 1=1
AND A.KNWLDG_NM LIKE '%' || #{searchKeyword} || '%'
AND A.KNWLDG_CN LIKE '%' || #{searchKeyword} || '%'
ORDER BY A.ANSWER_GROUP_NO DESC, A.ANSWER_ORDR ASC, A.ANSWER_DP ASC
) T0 ) WHERE RNUM BETWEEN #{firstIndex} + 1 AND #{firstIndex} + #{recordCountPerPage}

SELECT * FROM
(
SELECT KNWLDG_ID, PARNTS_KNWLDG_ID, ANSWER_ORDR
FROM COMTNDAMCALRES
WHERE 1=1
AND ANSWER_GROUP_NO = #{ansNumber}
AND PARNTS_KNWLDG_ID = #{ansParents}
ORDER BY ANSWER_ORDR DESC
)
WHERE ROWNUM = 1

SELECT NVL((MAX(ANSWER_ORDR)+1), 1) SEQ
FROM COMTNDAMCALRES
WHERE 1=1
AND ANSWER_GROUP_NO = #{ansNumber}
AND PARNTS_KNWLDG_ID = #{ansParents}

mapper.update:
UPDATE COMTNDAMCALRES
SET
	ANSWER_ORDR=ANSWER_ORDR+1
	WHERE 1=1
	AND ANSWER_GROUP_NO = #{ansNumber}
	AND ANSWER_ORDR >= #{ansSeq}
