mapper.select:
SELECT * FROM ( SELECT ROWNUM RNUM, ALL_LIST.* FROM (
SELECT AUTHOR_CODE, AUTHOR_NM, AUTHOR_DC, AUTHOR_CREAT_DE FROM COMTNAUTHORINFO WHERE 1=1
AND AUTHOR_NM LIKE '%'||#{searchKeyword}||'%'
ORDER BY AUTHOR_CREAT_DE DESC
) ALL_LIST ) WHERE RNUM > #{firstIndex} AND RNUM <= #{firstIndex} + #{recordCountPerPage}

mapper.insert:

 INSERT INTO COMTNAUTHORINFO 
       ( AUTHOR_CODE
       , AUTHOR_NM
       , AUTHOR_DC
       , AUTHOR_CREAT_DE )
VALUES ( #{authorCode}
       , #{authorNm}
       , #{authorDc}
       , SYSDATE)

mapper.update:

UPDATE COMTNAUTHORINFO 
   SET AUTHOR_NM=#{authorNm}
     , AUTHOR_DC=#{authorDc}
 WHERE AUTHOR_CODE=#{authorCode}

mapper.select:

SELECT AUTHOR_CODE, AUTHOR_NM, AUTHOR_DC, AUTHOR_CREAT_DE
  FROM COMTNAUTHORINFO 
 WHERE AUTHOR_CODE=#{authorCode}
