mapper.insert:
INSERT
  INTO tb_trx_info
(
       trxcode
	 , svcCode
	 , opMode
	 , deviceId
	 , branchName
	 , trxStsCode
	 , regDt
)
VALUES
(
       #{trxcode}
	 , #{svcCode}
	 , #{mode}
	 , #{deviceId}
	 , #{branchName}
	 , IFNULL(#{trxStsCode}, '0001')
	 , current_timestamp()
)

mapper.update:
UPDATE tb_trx_info
   SET trxStsCode = #{trxStsCode}

     , profileSendDt = current_timestamp()


     , vpReceptDt = current_timestamp()


	 , imgSendDt = current_timestamp()


     , nonce = #{nonce}


     , zkpNonce = #{zkpNonce}


     , errorCn = #{errorCn}

     , vpVerifyResult = IFNULL(#{vpVerifyResult}, 'N')
     , udtDt = current_timestamp()
     , vpName = #{vpName}
 WHERE trxcode = #{trxcode}

