mapper.insert:
INSERT
  INTO tb_trx_info
(
       trxcode
	 , svcCode
	 , opMode
	 , deviceId
	 , branchName
	 , trxStsCode
	 , regDt
)
VALUES
(
       #{trxcode}
	 , #{svcCode}
	 , #{mode}
	 , #{deviceId}
	 , #{branchName}
	 , COALESCE(#{trxStsCode}, '0001')
	 , NOW()
)

mapper.update:
UPDATE tb_trx_info
   SET trxStsCode = #{trxStsCode}

     , profileSendDt = NOW()


     , vpReceptDt = NOW()


	 , imgSendDt = NOW()


     , nonce = #{nonce}


     , zkpNonce = #{zkpNonce}


     , errorCn = #{errorCn}

     , vpVerifyResult = COALESCE(#{vpVerifyResult}, 'N')
     , udtDt = NOW()
     , vpName = #{vpName}
 WHERE trxcode = #{trxcode}

