mapper.select:
SELECT * FROM ( SELECT ROWNUM RNUM, ALL_LIST.* FROM ( /* 구현 Sql */ SELECT A.RESTDE_NO , A.RESTDE RESTDE_DE , A.RESTDE DBMS_KIND , A.RESTDE_NM , A.RESTDE_DC , B.CODE_NM RESTDE_SE , A.RESTDE_SE_CODE FROM COMTNRESTDE A , COMTCCMMNDETAILCODE B WHERE B.CODE_ID = 'COM017' AND A.RESTDE_SE_CODE = B.CODE
AND A.RESTDE = #{searchKeyword}
AND A.RESTDE_NM LIKE '%' || #{searchKeyword} || '%'
/* 구현 Sql */ ) ALL_LIST ) Z WHERE RNUM > #{firstIndex} AND RNUM <= #{firstIndex} + #{recordCountPerPage}

SELECT COUNT(*) totcnt FROM COMTNRESTDE A , COMTCCMMNDETAILCODE B WHERE B.CODE_ID = 'COM017' AND A.RESTDE_SE_CODE = B.CODE
AND RESTDE = #{searchKeyword}
AND RESTDE_NM LIKE '%' || #{searchKeyword} || '%'

mapper.insert:

INSERT
  INTO  COMTNRESTDE
