	 ,  RESTDE
	 ,  RESTDE_NM
	 ,  RESTDE_DC
	 ,  RESTDE_SE_CODE
     ,  FRST_REGIST_PNTTM
     ,  FRST_REGISTER_ID
     ,  LAST_UPDT_PNTTM
     ,  LAST_UPDUSR_ID
     )
VALUES
     (  #{restdeNo}
	 ,  #{restdeDe}
	 ,  #{restdeNm}
	 ,  #{restdeDc}
	 ,  #{restdeSeCode}
     ,  SYSDATETIME
     ,  #{frstRegisterId}
     ,  SYSDATETIME
     ,  #{frstRegisterId}
     )

mapper.update:

            UPDATE  COMTNRESTDE
               SET  RESTDE_NM         = #{restdeNm}
				 ,  RESTDE_DC         = #{restdeDc}
				 ,  RESTDE_SE_CODE    = #{restdeSeCode}
                 ,  LAST_UPDT_PNTTM = SYSDATETIME
                 ,  LAST_UPDUSR_ID    = #{lastUpdusrId}
             WHERE  RESTDE_NO         = #{restdeNo}
