mapper.select:
SELECT A.RESTDE_NO , A.RESTDE RESTDE_DE , A.RESTDE_NM , A.RESTDE_DC , B.CODE_NM RESTDE_SE , A.RESTDE_SE_CODE FROM COMTNRESTDE A , COMTCCMMNDETAILCODE B WHERE B.CODE_ID = 'COM017' AND A.RESTDE_SE_CODE = B.CODE
AND A.RESTDE = #{formattedDtKeyword}
AND A.RESTDE_NM LIKE CONCAT ('%', #{searchKeyword},'%')
LIMIT #{recordCountPerPage} OFFSET #{firstIndex}

SELECT COUNT(*) totcnt FROM COMTNRESTDE A , COMTCCMMNDETAILCODE B WHERE B.CODE_ID = 'COM017' AND A.RESTDE_SE_CODE = B.CODE
AND RESTDE = #{formattedDtKeyword}
AND RESTDE_NM LIKE CONCAT ('%', #{searchKeyword},'%')

mapper.insert:

INSERT
  INTO  COMTNRESTDE
     (  RESTDE_NO
	 ,  RESTDE
	 ,  RESTDE_NM
	 ,  RESTDE_DC
	 ,  RESTDE_SE_CODE
     ,  FRST_REGIST_PNTTM
     ,  FRST_REGISTER_ID
     ,  LAST_UPDT_PNTTM
     ,  LAST_UPDUSR_ID
     )
VALUES
     (  #{restdeNo}
	 ,  #{restdeDe}
	 ,  #{restdeNm}
	 ,  #{restdeDc}
	 ,  #{restdeSeCode}
     ,  NOW()
     ,  #{frstRegisterId}
     ,  NOW()
     ,  #{frstRegisterId}
     )

mapper.update:

            UPDATE  COMTNRESTDE
               SET  RESTDE_NM         = #{restdeNm}
				 ,  RESTDE_DC         = #{restdeDc}
				 ,  RESTDE_SE_CODE    = #{restdeSeCode}
                 ,  LAST_UPDT_PNTTM = NOW()
                 ,  LAST_UPDUSR_ID    = #{lastUpdusrId}
             WHERE  RESTDE_NO         = #{restdeNo}
