mapper.select:
SELECT * FROM ( SELECT ROWNUM RNUM, ALL_LIST.* FROM (
/* 구현 Sql */
SELECT ADMINIST_ZONE_SE , ADMINIST_ZONE_CODE , OCCRRNC_DE , OPERT_SN , CHANGE_SE_CODE , PROCESS_SE , ADMINIST_ZONE_NM , LOWEST_ADMINIST_ZONE_NM , CTPRVN_CODE , SIGNGU_CODE , EMD_CODE , LI_CODE , CREAT_DE , ABL_DE , ABL_ENNC FROM COMTCADMINISTCODERECPTNLOG WHERE 1 = 1
AND
ADMINIST_ZONE_NM LIKE '%' || #{searchKeyword} || '%'
AND
ADMINIST_ZONE_SE = #{administZoneSe}
AND
ADMINIST_ZONE_CODE = #{administZoneCode}
/* 구현 Sql */
) ALL_LIST ) WHERE RNUM > #{firstIndex} AND RNUM <= #{firstIndex} + #{recordCountPerPage}

mapper.insert:

INSERT
  INTO  COMTCADMINISTCODERECPTNLOG
     (  OCCRRNC_DE
     ,  ADMINIST_ZONE_SE
     ,  ADMINIST_ZONE_CODE
     ,  OPERT_SN
     ,  CHANGE_SE_CODE
     ,  PROCESS_SE
     ,  ADMINIST_ZONE_NM
     ,  LOWEST_ADMINIST_ZONE_NM
     ,  CTPRVN_CODE
     ,  SIGNGU_CODE
     ,  EMD_CODE
     ,  LI_CODE
     ,  CREAT_DE
     ,  ABL_DE
     ,  ABL_ENNC
     ,  FRST_REGISTER_ID
     ,  FRST_REGIST_PNTTM
     ,  LAST_UPDUSR_ID
     ,  LAST_UPDT_PNTTM
     )
VALUES
     (  #{occrrDe}
     ,  #{administZoneSe}
     ,  #{administZoneCode}
     ,  #{opertSn}
     ,  #{changeSeCode}
     ,  #{processSe}
     ,  #{administZoneNm}
     ,  #{lowestAdministZoneNm}
     ,  #{ctprvnCode}
     ,  #{signguCode}
     ,  #{emdCode}
     ,  #{liCode}
     ,  #{creatDe}
     ,  #{ablDe}
     ,  #{ablEnnc}
     ,  #{frstRegisterId}
     ,  sysdate
     ,  #{frstRegisterId}
     ,  sysdate
     )

mapper.update:

UPDATE  COMTCADMINISTCODERECPTNLOG
   SET  PROCESS_SE         = #{processSe}
     ,  LAST_UPDUSR_ID     = #{lastUpdusrId}
     ,  LAST_UPDT_PNTTM  = sysdate
 WHERE  OCCRRNC_DE           = #{occrrDe}
   AND  ADMINIST_ZONE_SE   = #{administZoneSe}
   AND  ADMINIST_ZONE_CODE = #{administZoneCode}
   AND  OPERT_SN           = #{opertSn}

