mapper.select:
SELECT * FROM ( SELECT ROWNUM RNUM, ALL_LIST.* FROM ( /* 구현 Sql */ SELECT CL_CODE , CL_CODE_NM , USE_AT FROM COMTCCMMNCLCODE WHERE 1 = 1
AND CL_CODE LIKE '%' || #{searchKeyword} || '%'
AND CL_CODE_NM LIKE '%' || #{searchKeyword} || '%'
AND USE_AT = 'Y'
/* 구현 Sql */
) ALL_LIST ) WHERE RNUM > #{firstIndex} AND RNUM <= #{firstIndex} + #{recordCountPerPage}

mapper.insert:


INSERT
  INTO  COMTCCMMNCLCODE
     (  CL_CODE
     ,  CL_CODE_NM
     ,  CL_CODE_DC
     ,  USE_AT
     ,  FRST_REGIST_PNTTM
     ,  FRST_REGISTER_ID
     ,  LAST_UPDT_PNTTM
     ,  LAST_UPDUSR_ID
     )
VALUES
     (  #{clCode}
     ,  #{clCodeNm}
     ,  #{clCodeDc}
     ,  #{useAt}
     ,  sysdate
     ,  #{frstRegisterId}
     ,  sysdate
     ,  #{frstRegisterId}
     )

mapper.update:


UPDATE  COMTCCMMNCLCODE
   SET  CL_CODE_NM        = #{clCodeNm}
     ,  CL_CODE_DC        = #{clCodeDc}
     ,  USE_AT            = #{useAt}
     ,  LAST_UPDT_PNTTM = sysdate
     ,  LAST_UPDUSR_ID    = #{lastUpdusrId}
 WHERE  CL_CODE           = #{clCode}

mapper.delete:


UPDATE  COMTCCMMNCLCODE
   SET  USE_AT  = 'N'
 WHERE  CL_CODE = #{clCode}
