mapper.select:
SELECT OCCRRNC_DE , INSTT_CODE , OPERT_SN , CHANGE_SE_CODE , PROCESS_SE , ETC_CODE , ALL_INSTT_NM , LOWEST_INSTT_NM , INSTT_ABRV_NM , ODR , ORD , INSTT_ODR , BEST_INSTT_CODE , UPPER_INSTT_CODE , REPRSNT_INSTT_CODE , INSTT_TY_LCLAS , INSTT_TY_MLSFC , INSTT_TY_SCLAS , TELNO , FXNUM , CREAT_DE , ABL_DE , ABL_ENNC , CHANGE_DE , CHANGE_TIME , BSIS_DE , SORT_ORDR FROM COMTNINSTTCODERECPTNLOG WHERE 1 = 1
AND
ALL_INSTT_NM LIKE CONCAT ('%', #{searchKeyword},'%')
AND
INSTT_CODE = #{insttCode}
LIMIT #{recordCountPerPage} OFFSET #{firstIndex}

mapper.insert:

INSERT
  INTO  COMTNINSTTCODERECPTNLOG
     (  OCCRRNC_DE
     ,  INSTT_CODE
     ,  OPERT_SN
     ,  CHANGE_SE_CODE
     ,  PROCESS_SE
     ,  ETC_CODE
     ,  ALL_INSTT_NM
     ,  LOWEST_INSTT_NM
     ,  INSTT_ABRV_NM
     ,  ODR
     ,  ORD
     ,  INSTT_ODR
     ,  BEST_INSTT_CODE
     ,  UPPER_INSTT_CODE
     ,  REPRSNT_INSTT_CODE
     ,  INSTT_TY_LCLAS
     ,  INSTT_TY_MLSFC
     ,  INSTT_TY_SCLAS
     ,  TELNO
     ,  FXNUM
     ,  CREAT_DE
     ,  ABL_DE
     ,  ABL_ENNC
     ,  CHANGE_DE
     ,  CHANGE_TIME
     ,  BSIS_DE
     ,  SORT_ORDR
     ,  FRST_REGISTER_ID
     ,  FRST_REGIST_PNTTM
     ,  LAST_UPDUSR_ID
     ,  LAST_UPDT_PNTTM
     )
VALUES
     (  #{occrrDe}
     ,  #{insttCode}
     ,  #{opertSn}
     ,  #{changeSeCode}
     ,  #{processSe}
     ,  #{etcCode}
     ,  #{allInsttNm}
     ,  #{lowestInsttNm}
     ,  #{insttAbrvNm}
     ,  #{odr}
     ,  #{ord}
     ,  #{insttOdr}
     ,  #{bestInsttCode}
     ,  #{upperInsttCode}
     ,  #{reprsntInsttCode}
     ,  #{insttTyLclas}
     ,  #{insttTyMclas}
     ,  #{insttTySclas}
     ,  #{telno}
     ,  #{fxnum}
     ,  #{creatDe}
     ,  #{ablDe}
     ,  #{ablEnnc}
     ,  #{changede}
     ,  #{changeTime}
     ,  #{bsisDe}
     ,  #{sortOrdr}
     ,  #{frstRegisterId}
     ,  NOW()
     ,  #{frstRegisterId}
     ,  NOW()
     )

mapper.update:

UPDATE  COMTNINSTTCODERECPTNLOG
   SET  PROCESS_SE         = #{processSe}
     ,  LAST_UPDUSR_ID     = #{lastUpdusrId}
     ,  LAST_UPDT_PNTTM  = NOW()
 WHERE  OCCRRNC_DE           = #{occrrDe}
   AND  INSTT_CODE         = #{insttCode}
   AND  OPERT_SN           = #{opertSn}

