mapper.insert:

SELECT CASE WHEN MAX(SN)+1 IS NULL THEN 1
       ELSE MAX(SN)+1 END AS sn 
	  FROM  COMTCZIP



	INSERT
	  INTO  COMTCZIP
	     (  ZIP
	     ,  SN
	     ,  CTPRVN_NM
	     ,  SIGNGU_NM
	     ,  EMD_NM
	     ,  LI_BULD_NM
	     ,  LNBR_DONG_HO
	     ,  FRST_REGIST_PNTTM
	     ,  FRST_REGISTER_ID
	     ,  LAST_UPDT_PNTTM
	     ,  LAST_UPDUSR_ID
	     )
	VALUES
	     (  #{zip}
	     ,  #{sn}
	     ,  #{ctprvnNm}
	     ,  #{signguNm}
	     ,  #{emdNm}
	     ,  #{liBuldNm}
	     ,  #{lnbrDongHo}
	     ,  SYSDATE()
	     ,  #{frstRegisterId}
	     ,  SYSDATE()
	     ,  #{frstRegisterId}
	     )


INSERT
  INTO  COMTCZIP
     (  ZIP
     ,  SN
     ,  CTPRVN_NM
     ,  SIGNGU_NM
     ,  EMD_NM
     ,  LI_BULD_NM
     ,  LNBR_DONG_HO
     ,  FRST_REGIST_PNTTM
     ,  FRST_REGISTER_ID
     ,  LAST_UPDT_PNTTM
     ,  LAST_UPDUSR_ID
     )
VALUES
     (  #{zip}
     ,  #{sn}
     ,  #{ctprvnNm}
     ,  #{signguNm}
     ,  #{emdNm}
     ,  #{liBuldNm}
     ,  #{lnbrDongHo}
     ,  SYSDATE()
     ,  #{frstRegisterId}
     ,  SYSDATE()
     ,  #{frstRegisterId}
     )

mapper.update:

UPDATE  COMTCZIP
   SET  CTPRVN_NM         = #{ctprvnNm}
     ,  SIGNGU_NM         = #{signguNm}
     ,  EMD_NM            = #{emdNm}
     ,  LI_BULD_NM        = #{liBuldNm}
     ,  LNBR_DONG_HO      = #{lnbrDongHo}
     ,  LAST_UPDT_PNTTM = sysdate()
     ,  LAST_UPDUSR_ID    = #{lastUpdusrId}
 WHERE  ZIP               = #{zip}
   AND  SN                = #{sn}
