mapper.select:
SELECT * FROM ( SELECT ROWNUM RNUM, ALL_LIST.* FROM (
SELECT SERVER_ID, SERVER_NM, SERVER_KND, (SELECT CD.CODE_NM FROM COMTCCMMNDETAILCODE CD WHERE CD.CODE_ID = 'COM064' AND CD.USE_AT = 'Y' AND SERVER_KND = CD.CODE) AS SERVER_KND_NM, TO_CHAR(RGSDE, 'yyyy-mm-dd') AS RGSDE, TO_CHAR(FRST_REGIST_PNTTM, 'yyyy-mm-dd HH24:mi:ss') AS FRST_REGIST_PNTTM, FRST_REGISTER_ID, TO_CHAR(LAST_UPDT_PNTTM, 'yyyy-mm-dd HH24:mi:ss') AS LAST_UPDT_PNTTM, LAST_UPDUSR_ID FROM COMTNSERVERINFO WHERE 1 = 1
AND SERVER_NM LIKE '%'||#{strServerNm}||'%'
ORDER BY SERVER_ID
) ALL_LIST ) WHERE RNUM > #{firstIndex} AND RNUM <= #{firstIndex} + #{recordCountPerPage}

mapper.delete:

DELETE FROM COMTNSERVEREQPMNRELATE
 WHERE SERVER_ID = #{serverId}
   AND SERVER_EQPMN_ID = #{serverEqpmnId}
