mapper.select:
SELECT TROBL_ID, TROBL_NM, TROBL_KND, (SELECT CODE_NM FROM COMTCCMMNDETAILCODE WHERE CODE_ID = 'COM065' AND USE_AT = 'Y' AND CODE = TROBL_KND) AS TROBL_KND_NM, TROBL_DC, DATE_FORMAT(TROBL_OCCRRNC_TIME, '%Y-%m-%d %T') AS TROBL_OCCRRNC_TIME, TROBL_RQESTER_NM, DATE_FORMAT(TROBL_REQUST_TIME, '%Y-%m-%d %T') AS TROBL_REQUST_TIME, TROBL_PROCESS_RESULT, TROBL_OPETR_NM, DATE_FORMAT(TROBL_PROCESS_TIME, '%Y-%m-%d %T') AS TROBL_PROCESS_TIME, PROCESS_STTUS, (SELECT CODE_NM FROM COMTCCMMNDETAILCODE WHERE CODE_ID = 'COM068' AND USE_AT = 'Y' AND CODE = PROCESS_STTUS) AS PROCESS_STTUS_NM, DATE_FORMAT(FRST_REGIST_PNTTM, '%Y-%m-%d %T') AS FRST_REGIST_PNTTM, FRST_REGISTER_ID, DATE_FORMAT(LAST_UPDT_PNTTM, '%Y-%m-%d %T') AS LAST_UPDT_PNTTM, LAST_UPDUSR_ID FROM COMTNTROBLINFO WHERE 1 = 1 AND PROCESS_STTUS IN ('R','C') AND TROBL_NM LIKE CONCAT('%' , #{strTroblNm}, '%')
AND TROBL_KND = #{strTroblKnd}
AND PROCESS_STTUS = #{strProcessSttus}
ORDER BY TROBL_ID LIMIT #{recordCountPerPage} OFFSET #{firstIndex}

SELECT COUNT(*) totcnt FROM COMTNTROBLINFO WHERE 1 = 1 AND PROCESS_STTUS IN ('R','C') AND TROBL_NM LIKE CONCAT('%' , #{strTroblNm}, '%')
AND TROBL_KND = #{strTroblKnd}
AND PROCESS_STTUS = #{strProcessSttus}
