mapper.select:
SELECT * FROM ( SELECT T0.*, ROWNUM RNUM FROM (
SELECT A.EVENT_ID, TO_CHAR(TO_DATE(A.EVENT_SVC_BGNDE, 'YYYYMMDD'),'YYYY-MM-DD') EVENT_SVC_BGNDE, A.SVC_USE_NMPR_CO, A.CHARGER_NM, A.EVENT_CN, A.FRST_REGIST_PNTTM, A.FRST_REGISTER_ID, (SELECT USER_NM FROM COMTNEMPLYRINFO WHERE ESNTL_ID = A.FRST_REGISTER_ID) FRST_REGISTER_NM, A.LAST_UPDT_PNTTM, A.LAST_UPDUSR_ID, TO_CHAR(TO_DATE(A.EVENT_SVC_ENDDE, 'YYYYMMDD'),'YYYY-MM-DD') EVENT_SVC_ENDDE, A.EVENT_TY_CODE, B.CODE_NM AS EVENT_TY_CODE_NM, A.PRPARETG_CN FROM COMTNEVENTINFO A LEFT OUTER JOIN (SELECT CODE_ID, CODE, CODE_NM FROM COMTCCMMNDETAILCODE WHERE CODE_ID = 'COM035' AND USE_AT='Y') b ON a.EVENT_TY_CODE = b.CODE WHERE 1=1
AND EVENT_CN LIKE '%' || #{searchKeyword} || '%'
AND FRST_REGISTER_ID IN (SELECT ESNTL_ID FROM COMTNEMPLYRINFO WHERE USER_NM LIKE '%' || #{searchKeyword} || '%' )
ORDER BY A.FRST_REGIST_PNTTM DESC
) T0 ) WHERE RNUM BETWEEN #{firstIndex} + 1 AND #{firstIndex} + #{recordCountPerPage}

SELECT * FROM ( SELECT T0.*, ROWNUM RNUM FROM (
SELECT A.SEXDSTN_CODE, B.CODE_NM AS SEXDSTN_CODE_NM, A.EXTRL_HR_NM, A.EMAIL_ADRES, A.OCCP_TY_CODE, C.CODE_NM AS OCCP_TY_CODE_NM, A.PSITN_INSTT_NM, A.EXTRL_HR_ID, A.EVENT_ID, A.BRTHDY, A.AREA_NO, A.MIDDLE_TELNO, A.END_TELNO, A.FRST_REGIST_PNTTM FRST_REGISTER_PNTTM, A.FRST_REGISTER_ID, (SELECT USER_NM FROM COMTNEMPLYRINFO WHERE ESNTL_ID = A.FRST_REGISTER_ID) FRST_REGISTER_NM, A.LAST_UPDT_PNTTM, A.LAST_UPDUSR_ID FROM COMTNEXTRLHRINFO A LEFT OUTER JOIN (SELECT CODE_ID, CODE, CODE_NM FROM COMTCCMMNDETAILCODE WHERE CODE_ID = 'COM014' AND USE_AT='Y') b ON a.SEXDSTN_CODE = b.CODE LEFT OUTER JOIN (SELECT CODE_ID, CODE, CODE_NM FROM COMTCCMMNDETAILCODE WHERE CODE_ID = 'COM034' AND USE_AT='Y') c ON a.OCCP_TY_CODE = c.CODE WHERE 1=1
AND A.EVENT_ID = #{eventId}
AND EXTRL_HR_NM LIKE '%' || #{searchKeyword} || '%'
AND PSITN_INSTT_NM LIKE '%' || #{searchKeyword} || '%'
ORDER BY A.FRST_REGIST_PNTTM DESC
) T0 ) WHERE RNUM BETWEEN #{firstIndex} + 1 AND #{firstIndex} + #{recordCountPerPage}
