mapper.update:
UPDATE COMTNNOTERECPTN 
SET
	OPEN_YN='Y',
	LAST_UPDT_PNTTM=sysdate,
	LAST_UPDUSR_ID=#{lastUpdusrId}
WHERE 1=1
	AND NOTE_ID=#{noteId}
	AND NOTE_TRNSMIT_ID=#{noteTrnsmitId}
	AND NOTE_RECPTN_ID=#{noteRecptnId}

mapper.select:
SELECT * FROM ( SELECT T0.*, ROWNUM RNUM FROM (
SELECT NOTE.NOTE_ID, NOTE_RNS.NOTE_TRNSMIT_ID, NOTE_RNS.NOTE_RECPTN_ID, NOTE_TNS.TRNSMITER_ID, NOTE.NOTE_SJ, NOTE.NOTE_CN, NOTE.ATCH_FILE_ID, NOTE_RNS.RCVER_ID, NOTE_RNS.OPEN_YN, NOTE_RNS.RECPTN_SE, (SELECT USER_NM FROM COMVNUSERMASTER WHERE ESNTL_ID = NOTE_TNS.TRNSMITER_ID) TRNSMITER_NM, (SELECT USER_NM FROM COMVNUSERMASTER WHERE ESNTL_ID = NOTE_RNS.RCVER_ID) RCVER_NM, (SELECT USER_ID FROM COMVNUSERMASTER WHERE ESNTL_ID = NOTE_RNS.RCVER_ID) RCVER_IDS, TO_CHAR(NOTE_RNS.FRST_REGIST_PNTTM,'YYYY-MM-DD hh24:mi:ss') FRST_REGISTER_PNTTM FROM COMTNNOTE NOTE LEFT OUTER JOIN COMTNNOTETRNSMIT NOTE_TNS ON ( NOTE.NOTE_ID=NOTE_TNS.NOTE_ID ) LEFT OUTER JOIN COMTNNOTERECPTN NOTE_RNS ON ( NOTE_TNS.NOTE_ID=NOTE_RNS.NOTE_ID AND NOTE_TNS.NOTE_TRNSMIT_ID=NOTE_RNS.NOTE_TRNSMIT_ID ) WHERE 1=1 AND NOTE_RNS.RCVER_ID = #{rcverId}
AND NOTE_RNS.RCVER_ID = (SELECT ESNTL_ID FROM COMTNEMPLYRINFO WHERE EMPLYR_ID = #{searchKeyword})
AND NOTE_RNS.RCVER_ID = (SELECT ESNTL_ID FROM COMTNEMPLYRINFO WHERE USER_NM = #{searchKeyword})
AND NOTE.NOTE_SJ LIKE '%' || #{searchKeyword} || '%'
AND NOTE.NOTE_CN LIKE '%' || #{searchKeyword} || '%'
AND TO_CHAR(NOTE_TNS.FRST_REGIST_PNTTM,'YYYY-MM-DD') >= #{searchFromDate} AND TO_CHAR(NOTE_TNS.FRST_REGIST_PNTTM,'YYYY-MM-DD') <= #{searchToDate}
ORDER BY NOTE.FRST_REGIST_PNTTM DESC
) T0 ) WHERE ROWNUM BETWEEN #{firstIndex} + 1 AND #{firstIndex} + #{recordCountPerPage}

SELECT COUNT(*) totcnt FROM COMTNNOTE NOTE LEFT OUTER JOIN COMTNNOTETRNSMIT NOTE_TNS ON ( NOTE.NOTE_ID=NOTE_TNS.NOTE_ID ) LEFT OUTER JOIN COMTNNOTERECPTN NOTE_RNS ON ( NOTE_TNS.NOTE_ID=NOTE_RNS.NOTE_ID AND NOTE_TNS.NOTE_TRNSMIT_ID=NOTE_RNS.NOTE_TRNSMIT_ID ) WHERE 1=1 AND NOTE_RNS.RCVER_ID = #{rcverId}
AND NOTE_RNS.RCVER_ID = (SELECT ESNTL_ID FROM COMTNEMPLYRINFO WHERE EMPLYR_ID = #{searchKeyword})
AND NOTE_RNS.RCVER_ID = (SELECT ESNTL_ID FROM COMTNEMPLYRINFO WHERE USER_NM = #{searchKeyword})
AND NOTE.NOTE_SJ LIKE '%' || #{searchKeyword} || '%'
AND NOTE.NOTE_CN LIKE '%' || #{searchKeyword} || '%'
AND TO_CHAR(NOTE_TNS.FRST_REGIST_PNTTM,'YYYY-MM-DD') >= #{searchFromDate} AND TO_CHAR(NOTE_TNS.FRST_REGIST_PNTTM,'YYYY-MM-DD') <= #{searchToDate}

SELECT 
COUNT(NOTE_ID) CNT
FROM COMTNNOTERECPTN  
WHERE 1=1 
AND NOTE_ID=#{noteId}
AND NOTE_TRNSMIT_ID=#{noteTrnsmitId}

mapper.delete:

DELETE FROM COMTNNOTERECPTN 
WHERE 1=1 
AND NOTE_ID=#{noteId} AND 
NOTE_TRNSMIT_ID=#{noteTrnsmitId} 
AND NOTE_RECPTN_ID=#{noteRecptnId} 
AND RCVER_ID = #{rcverId}


DELETE FROM COMTNNOTETRNSMIT 
WHERE 1=1 
AND NOTE_ID=#{noteId}
AND NOTE_TRNSMIT_ID=#{noteTrnsmitId}
AND DELETE_AT = 'Y'



DELETE FROM COMTNNOTERECPTN 
WHERE 1=1 
AND NOTE_ID=#{noteId}
AND NOTE_TRNSMIT_ID=#{noteTrnsmitId} 
AND NOTE_RECPTN_ID=#{noteRecptnId};

DELETE FROM COMTNNOTETRNSMIT
WHERE 1=1 
AND NOTE_ID=#{noteId}
AND NOTE_TRNSMIT_ID=#{noteTrnsmitId};

DELETE FROM COMTNNOTE WHERE NOTE_ID=#{noteId};
