mapper.select:
SELECT * FROM ( SELECT T0.*, ROWNUM RNUM FROM (
SELECT NOTE.NOTE_ID, NOTE_TNS.NOTE_TRNSMIT_ID, NOTE_TNS.TRNSMITER_ID, NOTE.NOTE_SJ, NOTE.NOTE_CN, NOTE.ATCH_FILE_ID, TO_CHAR(NOTE_TNS.FRST_REGIST_PNTTM,'YYYY-MM-DD hh24:mi:ss') FRST_REGISTER_PNTTM, ( SELECT (SELECT USER_NM FROM COMTNEMPLYRINFO WHERE ESNTL_ID = A.RCVER_ID) FROM COMTNNOTERECPTN A WHERE 1=1 AND A.NOTE_ID=NOTE_TNS.NOTE_ID AND A.NOTE_TRNSMIT_ID=NOTE_TNS.NOTE_TRNSMIT_ID AND ROWNUM <= 1 ) RCVER_NM, ( SELECT COUNT(A.NOTE_ID) FROM COMTNNOTERECPTN A WHERE 1=1 AND A.NOTE_ID=NOTE_TNS.NOTE_ID AND A.NOTE_TRNSMIT_ID=NOTE_TNS.NOTE_TRNSMIT_ID )-1 RCVER_CNT, ( SELECT COUNT(A.NOTE_ID) FROM COMTNNOTERECPTN A WHERE 1=1 AND A.NOTE_ID=NOTE_TNS.NOTE_ID AND A.NOTE_TRNSMIT_ID=NOTE_TNS.NOTE_TRNSMIT_ID AND A.OPEN_YN = 'Y' ) OPEN_Y, ( SELECT COUNT(A.NOTE_ID) FROM COMTNNOTERECPTN A WHERE 1=1 AND A.NOTE_ID=NOTE_TNS.NOTE_ID AND A.NOTE_TRNSMIT_ID=NOTE_TNS.NOTE_TRNSMIT_ID AND A.OPEN_YN = 'N' ) OPEN_N FROM COMTNNOTE NOTE, COMTNNOTETRNSMIT NOTE_TNS WHERE 1=1 AND NOTE.NOTE_ID = NOTE_TNS.NOTE_ID AND NOTE_TNS.DELETE_AT = 'N' AND NOTE_TNS.TRNSMITER_ID=#{trnsmiterId}
AND NOTE.NOTE_SJ LIKE '%' || #{searchKeyword} || '%'
AND NOTE.NOTE_CN LIKE '%' || #{searchKeyword} || '%'
AND TO_CHAR(NOTE_TNS.FRST_REGIST_PNTTM,'YYYY-MM-DD') >= #{searchFromDate} AND TO_CHAR(NOTE_TNS.FRST_REGIST_PNTTM,'YYYY-MM-DD') <= #{searchToDate}
ORDER BY NOTE.FRST_REGIST_PNTTM DESC
) T0 ) WHERE ROWNUM BETWEEN #{firstIndex} + 1 AND #{firstIndex} + #{recordCountPerPage}

SELECT COUNT(*) totcnt FROM COMTNNOTE NOTE, COMTNNOTETRNSMIT NOTE_TNS WHERE 1=1 AND NOTE.NOTE_ID = NOTE_TNS.NOTE_ID AND NOTE_TNS.DELETE_AT = 'N' AND NOTE_TNS.TRNSMITER_ID=#{trnsmiterId}
AND NOTE.NOTE_SJ LIKE '%' || #{searchKeyword} || '%'
AND NOTE.NOTE_CN LIKE '%' || #{searchKeyword} || '%'
AND TO_CHAR(NOTE_TNS.FRST_REGIST_PNTTM,'YYYY-MM-DD') >= #{searchFromDate} AND TO_CHAR(NOTE_TNS.FRST_REGIST_PNTTM,'YYYY-MM-DD') <= #{searchToDate}

mapper.delete:

UPDATE COMTNNOTETRNSMIT SET DELETE_AT='Y',
LAST_UPDT_PNTTM=sysdate,
LAST_UPDUSR_ID=#{lastUpdusrId}
WHERE 1=1 
AND NOTE_ID=#{noteId}
AND NOTE_TRNSMIT_ID=#{noteTrnsmitId}


DELETE FROM COMTNNOTERECPTN 
WHERE 1=1 
AND NOTE_ID=#{noteId}
AND NOTE_TRNSMIT_ID=#{noteTrnsmitId} 
AND NOTE_RECPTN_ID=#{noteRecptnId}

mapper.select:

SELECT 
COUNT(NOTE_ID) CNT
FROM COMTNNOTERECPTN
WHERE 1=1 
AND NOTE_ID=#{noteId}
AND NOTE_TRNSMIT_ID=#{noteTrnsmitId}

mapper.delete:

