mapper.select:
SELECT @RNUM:=@RNUM+1 AS rowCount , A.RWARD_ID AS rwardId , A.RWARDWNR_ID AS rwardManId , A.RWARD_CODE AS rwardCd , A.RWARD_DE AS rwardDe , A.RWARD_NM AS rwardNm , A.PBLEN_CN AS pblenCn , A.SANCTNER_ID AS sanctnerId , A.CONFM_AT AS confmAt , A.SANCTN_DT AS sanctnDt , A.RETURN_RESN AS returnResn , A.ATCH_FILE_ID AS atchFileId , A.INFRML_SANCTN_ID AS infrmlSanctnId , C.USER_NM AS rwardManNm , (select mst.USER_NM from COMVNUSERMASTER mst where mst.ESNTL_ID = SANCTNER_ID) AS sanctnerNm , (select code_nm from COMTCCMMNDETAILCODE where code_id ='COM055' and code = A.RWARD_CODE) AS rwardCdNm , (select info.ORGNZT_NM from COMTNORGNZTINFO info where info.ORGNZT_ID = C.ORGNZT_ID ) AS orgnztNm FROM (select @RNUM:=0) R, COMTNRWARDMANAGE A, COMVNUSERMASTER C WHERE 1=1 AND A.RWARDWNR_ID = C.ESNTL_ID AND A.RWARD_CODE = #{searchKeyword}
AND A.RWARD_DE BETWEEN CAST( DATE_FORMAT(#{searchFromDate},'%Y%m%d' ) AS CHAR(8) ) AND CAST( DATE_FORMAT(#{searchToDate},'%Y%m%d' ) AS CHAR(8) )
AND C.USER_NM like CONCAT('%', #{searchNm}, '%')
ORDER BY FRST_REGIST_PNTTM DESC LIMIT #{recordCountPerPage} OFFSET #{firstIndex}

SELECT COUNT(*) totcnt FROM COMTNRWARDMANAGE A, COMVNUSERMASTER C WHERE 1=1 AND A.RWARDWNR_ID = C.ESNTL_ID AND A.RWARD_CODE = #{searchKeyword}
AND A.RWARD_DE BETWEEN CAST( DATE_FORMAT(#{searchFromDate},'%Y%m%d' ) AS CHAR(8) ) AND CAST( DATE_FORMAT(#{searchToDate},'%Y%m%d' ) AS CHAR(8) )
AND C.USER_NM like CONCAT('%', #{searchNm}, '%')

SELECT @RNUM:=@RNUM+1 AS rowCount , A.RWARD_ID AS rwardId , A.RWARDWNR_ID AS rwardManId , A.RWARD_CODE AS rwardCd , A.RWARD_DE AS rwardDe , A.RWARD_NM AS rwardNm , A.PBLEN_CN AS pblenCn , A.SANCTNER_ID AS sanctnerId , A.CONFM_AT AS confmAt , A.SANCTN_DT AS sanctnDt , A.RETURN_RESN AS returnResn , A.ATCH_FILE_ID AS atchFileId , A.INFRML_SANCTN_ID AS infrmlSanctnId , C.USER_NM AS rwardManNm , (select mst.USER_NM from COMVNUSERMASTER mst where mst.ESNTL_ID = SANCTNER_ID) AS sanctnerNm , (select code_nm from COMTCCMMNDETAILCODE where code_id ='COM055' and code = A.RWARD_CODE) AS rwardCdNm , (select info.ORGNZT_NM from COMTNORGNZTINFO info where info.ORGNZT_ID = C.ORGNZT_ID ) AS orgnztNm FROM (select @RNUM:=0) R, COMTNRWARDMANAGE A, COMVNUSERMASTER C WHERE 1=1 AND A.SANCTNER_ID = #{sanctnerId} AND A.RWARDWNR_ID = C.ESNTL_ID AND A.RWARD_CODE = #{searchKeyword}
AND A.RWARD_DE BETWEEN CAST( DATE_FORMAT(#{searchFromDate},'%Y%m%d' ) AS CHAR(8) ) AND CAST( DATE_FORMAT(#{searchToDate},'%Y%m%d' ) AS CHAR(8) )
AND C.USER_NM like CONCAT('%', #{searchNm}, '%')
AND A.CONFM_AT like CONCAT('%', #{searchConfmAt}, '%')
ORDER BY FRST_REGIST_PNTTM DESC LIMIT #{recordCountPerPage} OFFSET #{firstIndex}
