mapper.update:


            UPDATE  COMTNVCATNMANAGE
               SET  VCATN_RESN         = #{vcatnResn},   
					LAST_UPDUSR_ID     = #{lastUpdusrId},
                    LAST_UPDT_PNTTM  = NOW()    
             WHERE  APPLCNT_ID         = #{applcntId}
			 AND    VCATN_SE           = #{vcatnSe}
			 AND    BGNDE			   = #{bgnde}
			 AND    ENDDE			   = #{endde}

mapper.delete:


             DELETE FROM COMTNVCATNMANAGE
             WHERE  APPLCNT_ID     = #{applcntId}
			 AND    VCATN_SE       = #{vcatnSe}
			 AND    BGNDE		  = #{bgnde}
			 AND    ENDDE		  = #{endde}

mapper.select:
SELECT ROW_NUMBER() OVER () as rowCount , A.APPLCNT_ID AS applcntId , mst.USER_NM AS applcntNm , (select info.ORGNZT_NM from COMTNORGNZTINFO info where info.ORGNZT_ID = mst.ORGNZT_ID ) AS orgnztNm, A.VCATN_SE AS vcatnSe , (select code_nm from COMTCCMMNDETAILCODE where code_id ='COM056' and code = A.VCATN_SE) as vcatnSeNm, A.REQST_DE AS reqstDe , A.SANCTNER_ID AS sanctnerId , A.CONFM_AT AS confmAt , A.SANCTN_DT AS sanctnDt , A.RETURN_RESN AS returnResn , A.BGNDE AS bgnde , A.ENDDE AS endde , A.OCCRRNC_YEAR AS occrrncYear , A.VCATN_RESN AS vcatnResn , A.INFRML_SANCTN_ID AS infrmlSanctnId , A.NOON_SE AS noonSe FROM COMTNVCATNMANAGE A, COMVNUSERMASTER mst WHERE 1=1 AND mst.ESNTL_ID = A.APPLCNT_ID AND A.SANCTNER_ID = #{sanctnerId}
AND mst.USER_NM like CONCAT('%', #{searchNm}, '%')
AND A.CONFM_AT = #{searchConfmAt}
AND #{searchKeyword} BETWEEN CAST(TO_CHAR(TO_DATE(A.BGNDE,'YYYYmmdd'),'YYYY-mm-dd') AS CHAR(6)) AND CAST(TO_CHAR(TO_DATE(A.ENDDE ,'YYYYmmdd' ),'YYYY-mm-dd') AS CHAR(6) )
AND A.BGNDE like CONCAT('%', #{searchKeyword}, '%')
ORDER BY A.FRST_REGIST_PNTTM DESC LIMIT #{recordCountPerPage} OFFSET #{firstIndex}

SELECT COUNT(*) totcnt FROM COMTNVCATNMANAGE A, COMVNUSERMASTER mst WHERE 1=1 AND mst.ESNTL_ID = A.APPLCNT_ID AND A.SANCTNER_ID = #{sanctnerId}
AND mst.USER_NM like CONCAT('%', #{searchNm}, '%')
AND A.CONFM_AT = #{searchConfmAt}
AND #{searchKeyword} BETWEEN CAST(TO_CHAR(TO_DATE(A.BGNDE,'YYYYmmdd'),'YYYY-mm-dd') AS CHAR(6)) AND CAST(TO_CHAR(TO_DATE(A.ENDDE ,'YYYYmmdd' ),'YYYY-mm-dd') AS CHAR(6) )
AND A.BGNDE like CONCAT('%', #{searchKeyword}, '%')

mapper.update:

            UPDATE COMTNVCATNMANAGE
               SET  CONFM_AT           = #{confmAt},   
                    SANCTN_DT          = #{sanctnDt}::timestamp,  
                    RETURN_RESN        = #{returnResn},  
					LAST_UPDUSR_ID     = #{lastUpdusrId},
                    LAST_UPDT_PNTTM  = NOW()    
             WHERE  APPLCNT_ID         = #{applcntId}
			 AND    VCATN_SE           = #{vcatnSe}
			 AND    BGNDE			   = #{bgnde}
			 AND    ENDDE			   = #{endde}
