mapper.select:
SELECT * FROM ( SELECT T0.*, ROWNUM RNUM FROM (
SELECT A.ADMINIST_WORD_ID, A.ADMINIST_WORD_NM, A.ADMINIST_WORD_ENG_NM, A.ADMINIST_WORD_ABRV_NM ADMINIST_WORD_ABRV, A.THEMA_RELM, A.WORD_SE , C.CODE_NM AS WORD_SE_NM, A.RELATE_STD_WORD, A.ADMINIST_WORD_DFN, A.ADMINIST_WORD_DC, A.FRST_REGISTER_ID, TO_CHAR(A.FRST_REGIST_PNTTM,'YYYY-MM-DD') FRST_REGISTER_PNTTM, (SELECT USER_NM FROM COMTNEMPLYRINFO WHERE ESNTL_ID = A.FRST_REGISTER_ID) FRST_REGISTER_NM FROM COMTNADMINISTRATIONWORD A LEFT OUTER JOIN (SELECT CODE_ID, CODE, CODE_NM FROM COMTCCMMNDETAILCODE WHERE CODE_ID = 'COM102' AND USE_AT='Y') c ON a.WORD_SE = c.CODE WHERE 1=1
AND ADMINIST_WORD_NM LIKE '%' || #{searchKeyword} || '%'
AND ADMINIST_WORD_ENG_NM LIKE '%' || #{searchKeyword} || '%'
AND ADMINIST_WORD_ABRV_NM LIKE '%' || #{searchKeyword} || '%'
AND ADMINIST_WORD_DFN LIKE '%' || #{searchKeyword} || '%'
AND ADMINIST_WORD_DC LIKE '%' || #{searchKeyword} || '%'
AND A.ADMINIST_WORD_NM >= #{choseongA} AND A.ADMINIST_WORD_NM < #{choseongB}
AND SUBSTR(A.ADMINIST_WORD_NM, 0, 1) >= #{choseongA} AND SUBSTR(A.ADMINIST_WORD_NM, 0, 1) <= #{choseongB}
ORDER BY A.FRST_REGIST_PNTTM DESC
) T0 ) WHERE RNUM BETWEEN #{firstIndex} + 1 AND #{firstIndex} + #{recordCountPerPage}
