mapper.insert:

INSERT INTO COMTNCNSLTLIST
	   (CNSLT_ID
	  , CNSLT_SJ
	  , CNSLT_CN
	  , OTHBC_AT
	  , WRITNG_PASSWORD
	  , AREA_NO
	  , MIDDLE_TELNO
	  , END_TELNO
	  , FRST_MBTLNUM
	  , MIDDLE_MBTLNUM
	  , END_MBTLNUM
	  , EMAIL_ADRES
	  , EMAIL_ANSWER_AT
	  , WRTER_NM
	  , WRITNG_DE
	  , RDCNT
	  , QNA_PROCESS_STTUS_CODE
	  , ATCH_FILE_ID
	  , FRST_REGIST_PNTTM
	  , FRST_REGISTER_ID)
VALUES ( #{cnsltId}
	  , #{cnsltSj}
	  , #{cnsltCn}
	  , #{othbcAt}
	  , #{writngPassword}
	  , #{areaNo}
	  , #{middleTelno}
	  , #{endTelno}
	  , #{firstMoblphonNo}
	  , #{middleMbtlnum}
	  , #{endMbtlnum}
	  , #{emailAdres}
	  , #{emailAnswerAt}
	  , #{wrterNm}
	  , sysdate
	  , 0
	  , '1'
	  , #{atchFileId}
	  , sysdate
	  , #{frstRegisterId})

mapper.update:

			UPDATE	COMTNCNSLTLIST	SET
				    RDCNT= NVL(RDCNT,0) + 1
 			WHERE   CNSLT_ID=#{cnsltId}

mapper.select:

SELECT 	COUNT(*) totcnt
FROM 	COMTNCNSLTLIST
WHERE	CNSLT_ID=#{cnsltId}
AND		WRITNG_PASSWORD=#{writngPassword}

mapper.update:

UPDATE	COMTNCNSLTLIST	SET
	    CNSLT_SJ=#{cnsltSj}
