mapper.delete:

DELETE FROM COMTNQUSTNRRESPONDINFO
WHERE 1=1
AND QUSTNR_RESPOND_ID = #{qestnrRespondId}

mapper.select:
SELECT * FROM ( SELECT T0.*, FLOOR((ROWNUM - 1) / #{pageSize} + 1) PAGE FROM (
SELECT ( SELECT QUSTNR_SJ FROM COMTNQESTNRINFO WHERE 1=1 AND QESTNR_ID = A.QESTNR_ID ) QESTNR_SJ, A.QESTNR_ID, A.QUSTNR_RESPOND_ID AS QESTNR_RESPOND_ID, A.SEXDSTN_CODE, A.OCCP_TY_CODE, A.RESPOND_NM, A.BRTHDY, A.AREA_NO, A.MIDDLE_TELNO, A.END_TELNO, A.QUSTNR_TMPLAT_ID AS QESTNR_TMPLAT_ID, A.FRST_REGIST_PNTTM AS FRST_REGIST_PNTTM, A.FRST_REGISTER_ID, B.USER_NM AS FRST_REGISTER_NM, A.LAST_UPDT_PNTTM, A.LAST_UPDUSR_ID FROM COMTNQUSTNRRESPONDINFO A LEFT OUTER JOIN COMVNUSERMASTER B ON B.ESNTL_ID = A.FRST_REGISTER_ID WHERE 1=1
AND A.RESPOND_NM LIKE '%' || #{searchKeyword} || '%'
AND B.USER_NM LIKE '%' || #{searchKeyword} || '%'
ORDER BY A.FRST_REGIST_PNTTM DESC
) T0 ) Z WHERE PAGE = #{pageIndex}
