mapper.select:
SELECT * FROM ( SELECT T0.*, ROWNUM RNUM FROM (
SELECT A.INDVDL_INFO_POLICY_ID, A.INDVDL_INFO_POLICY_NM, A.INDVDL_INFO_POLICY_CN, A.INDVDL_INFO_POLICY_AGRE_AT INDVDL_INFOSCR_POLICY_AGRE_AT, A.FRST_REGISTER_ID, TO_CHAR(A.FRST_REGIST_PNTTM,'YYYY-MM-DD') FRST_REGIST_PNTTM, (SELECT USER_NM FROM COMVNUSERMASTER WHERE ESNTL_ID = A.FRST_REGISTER_ID) FRST_REGISTER_NM FROM COMTNINDVDLINFOPOLICY A WHERE 1=1
AND INDVDL_INFO_POLICY_NM LIKE '%' || #{searchKeyword} || '%'
AND INDVDL_INFO_POLICY_CN LIKE '%' || #{searchKeyword} || '%'
ORDER BY A.FRST_REGIST_PNTTM DESC
) T0 ) WHERE RNUM BETWEEN #{firstIndex} + 1 AND #{firstIndex} + #{recordCountPerPage}
