mapper.select:
SELECT * FROM ( SELECT rownum rn, TB.* FROM ( SELECT ESNTL_ID as "uniqId", 'USR01' as "userTy", MBER_ID as "userId", MBER_NM as "userNm", MBER_EMAIL_ADRES as "emailAdres", AREA_NO as "areaNo", MIDDLE_TELNO as "middleTelno", END_TELNO as "endTelno", MBTLNUM as "moblphonNo", GROUP_ID as "groupId", MBER_STTUS as "sttus", SBSCRB_DE as "sbscrbDe" FROM COMTNGNRLMBER WHERE 1=1
AND MBER_STTUS LIKE #{sbscrbSttus}
AND MBER_ID LIKE #{searchKeyword}
AND MBER_NM LIKE '%' ||#{searchKeyword}|| '%'
ORDER BY 12 DESC ) TB ) WHERE rn BETWEEN #{firstIndex} + 1 AND #{firstIndex} + #{recordCountPerPage}

SELECT COUNT(1) totcnt
FROM   COMTNGNRLMBER
WHERE 1=1

    MBER_STTUS LIKE  #{sbscrbSttus}



    AND MBER_ID LIKE #{searchKeyword}


    AND MBER_NM LIKE '%' ||#{searchKeyword}|| '%'

mapper.update:

UPDATE COMTNGNRLMBER 
SET 
       PASSWORD   =  #{password}
     , CHG_PWD_LAST_PNTTM = sysdate
WHERE  ESNTL_ID  = #{uniqId}
