mapper.select:
SELECT ESNTL_ID "uniqId", 'USR03' "userTy", EMPLYR_ID "userId", USER_NM "userNm", EMAIL_ADRES "emailAdres", AREA_NO "areaNo", HOUSE_MIDDLE_TELNO "middleTelno", HOUSE_END_TELNO "endTelno", MBTLNUM "moblphonNo", GROUP_ID "groupId", EMPLYR_STTUS_CODE "sttus", SBSCRB_DE "sbscrbDe" FROM COMTNEMPLYRINFO WHERE 1=1
AND EMPLYR_STTUS_CODE LIKE #{sbscrbSttus}
AND EMPLYR_ID LIKE '%' || #{searchKeyword} || '%'
AND USER_NM LIKE '%' || #{searchKeyword} || '%'
ORDER BY SBSCRB_DE DESC LIMIT #{recordCountPerPage} OFFSET #{firstIndex}

mapper.update:

UPDATE COMTNEMPLYRINFO 
SET 
       PASSWORD   =  #{password}
     , CHG_PWD_LAST_PNTTM = NOW()
WHERE  ESNTL_ID  = #{uniqId}
