mapper.select:
SELECT A.SYS_ID , A.WEBSVC_KND , A.SITE_URL , B.CODE_NM HTTP_STTUS_CODE , DATE_FORMAT(A.CREAT_DT,'%Y-%m-%d %H:%i:%s') CREAT_DT , A.MNGR_NM , A.MNGR_EMAIL_ADRES FROM COMTNHTTPMON A LEFT OUTER JOIN COMTCCMMNDETAILCODE B ON B.CODE_ID = 'COM072' AND A.HTTP_STTUS_CODE = B.CODE WHERE 1 = 1 AND A.DELETE_AT = 'N'
AND binary(A.MNGR_NM) LIKE CONCAT('%', #{searchKeyword}, '%')
AND B.CODE_NM = #{searchKeyword}
LIMIT #{recordCountPerPage} OFFSET #{firstIndex}

SELECT COUNT(A.SYS_ID) totcnt FROM COMTNHTTPMON A LEFT OUTER JOIN COMTCCMMNDETAILCODE B ON B.CODE_ID = 'COM072' AND A.HTTP_STTUS_CODE = B.CODE WHERE 1 = 1 AND A.DELETE_AT = 'N'
AND binary(A.MNGR_NM) LIKE CONCAT('%', #{searchKeyword}, '%')
AND B.CODE_NM = #{searchKeyword}

mapper.insert:

    INSERT 
      INTO  COMTNHTTPMON
         (  SYS_ID
         ,  WEBSVC_KND
         ,  SITE_URL
         ,  HTTP_STTUS_CODE
         ,  CREAT_DT		         
         ,  MNGR_NM
         ,  MNGR_EMAIL_ADRES
	     ,	FRST_REGISTER_ID
	     ,	FRST_REGIST_PNTTM
         ,  LAST_UPDUSR_ID			     	         
         ,  LAST_UPDT_PNTTM
         ,	DELETE_AT
         ) 
    VALUES  
         (  #{sysId}
         ,  #{webKind}
         ,  #{siteUrl}
         ,  #{httpSttusCd}
         ,  sysdate()		         
         ,  #{mngrNm}
         ,  #{mngrEmailAddr}
         ,  #{frstRegisterId}
         ,  sysdate()		         		         
         ,  #{lastUpdusrId}
         ,  sysdate()
         ,	'N'
         )

mapper.update:

UPDATE  COMTNHTTPMON 
   SET  WEBSVC_KND          = #{webKind}
     ,  SITE_URL          = #{siteUrl}
   	 ,  MNGR_NM        	  = #{mngrNm}
     ,  MNGR_EMAIL_ADRES   = #{mngrEmailAddr}
     ,  LAST_UPDT_PNTTM = sysdate()
     ,  LAST_UPDUSR_ID    = #{lastUpdusrId}
 WHERE  SYS_ID            = #{sysId}

mapper.delete:

            UPDATE  COMTNHTTPMON
			   SET  DELETE_AT		= 'Y'
			 WHERE  SYS_ID			= #{sysId}

mapper.select:
SELECT A.LOG_ID , A.SYS_ID , A.WEBSVC_KND , B.SITE_URL , C.CODE_NM HTTP_STTUS_CODE , DATE_FORMAT(A.CREAT_DT,'%Y-%m-%d %H:%i:%s') CREAT_DT , A.LOG_INFO , A.MNGR_NM , A.MNGR_EMAIL_ADRES FROM COMTHHTTPMONLOGINFO A, COMTNHTTPMON B, COMTCCMMNDETAILCODE C WHERE A.SYS_ID = B.SYS_ID AND A.HTTP_STTUS_CODE = C.CODE AND C.CODE_ID = 'COM072' AND 1 = 1
AND A.CREAT_DT BETWEEN #{searchBgnDt} AND #{searchEndDt}
AND binary(A.WEBSVC_KND) LIKE CONCAT('%', #{searchKeyword}, '%')
AND C.CODE_NM = #{searchKeyword}
ORDER BY A.FRST_REGIST_PNTTM DESC LIMIT #{recordCountPerPage} OFFSET #{firstIndex}

SELECT COUNT(A.SYS_ID) totcnt FROM COMTHHTTPMONLOGINFO A, COMTNHTTPMON B, COMTCCMMNDETAILCODE C WHERE A.SYS_ID = B.SYS_ID AND A.HTTP_STTUS_CODE = C.CODE AND C.CODE_ID = 'COM072' AND 1 = 1
AND A.CREAT_DT BETWEEN #{searchBgnDt} AND #{searchEndDt}
AND binary(A.WEBSVC_KND) LIKE CONCAT('%', #{searchKeyword}, '%')
AND C.CODE_NM = #{searchKeyword}

mapper.insert:

    INSERT 
      INTO  COMTHHTTPMONLOGINFO
         (  LOG_ID
         ,  SYS_ID
         ,  WEBSVC_KND
         ,  SITE_URL
         ,  HTTP_STTUS_CODE		         
         ,  CREAT_DT
         ,  LOG_INFO		         
         ,  MNGR_NM
         ,  MNGR_EMAIL_ADRES
	     ,	FRST_REGISTER_ID
	     ,	FRST_REGIST_PNTTM
         ,  LAST_UPDUSR_ID			     	         
         ,  LAST_UPDT_PNTTM
         ) 
    VALUES  
         (  #{logId}
         ,  #{sysId}
         ,  #{webKind}
         ,  #{siteUrl}
         ,  #{httpSttusCd}		         
         ,  #{creatDt}
         ,  #{logInfo}		         
         ,  #{mngrNm}
         ,  #{mngrEmailAddr}
         ,  #{frstRegisterId}
         ,  #{frstRegisterPnttm}		         		         
         ,  #{lastUpdusrId}
         ,  sysdate()
         )

mapper.update:

UPDATE  COMTNHTTPMON 
   SET  HTTP_STTUS_CODE     = #{httpSttusCd}
     ,  CREAT_DT          = #{creatDt}
     ,  LAST_UPDT_PNTTM = sysdate()
     ,  LAST_UPDUSR_ID    = #{lastUpdusrId}
 WHERE  SYS_ID            = #{sysId}
