mapper.delete:


DELETE FROM COMTNNTWRKSVCMNTRNG 
WHERE SYS_IP = #{sysIp}
AND SYS_PORT = #{sysPort}

mapper.select:
 	SELECT 
		COUNT(SYS_IP) as cnt
	FROM COMTNNTWRKSVCMNTRNG 
	WHERE SYS_IP = #{sysIp}
		AND SYS_PORT = #{sysPort}

mapper.update:

UPDATE COMTNNTWRKSVCMNTRNG 
SET  
	MNTRNG_STTUS=#{mntrngSttus},
	CREAT_DT=TO_DATE(#{creatDt}, 'yyyymmddhh24miss'),
	LAST_UPDT_PNTTM = sysdate,
	LAST_UPDUSR_ID = #{lastUpdusrId}
WHERE SYS_IP = #{sysIp}
	AND SYS_PORT = #{sysPort}

mapper.select:
SELECT * FROM ( SELECT T0.*, ROWNUM RNUM FROM (
SELECT A.SYS_IP, A.SYS_PORT, A.LOG_ID, A.SYS_NM, C.CODE_NM MNTRNG_STTUS, A.CREAT_DT FROM COMTNNTWRKSVCMNTRNGLOGINFO A LEFT OUTER JOIN COMTNNTWRKSVCMNTRNG B ON A.SYS_IP = B.SYS_IP AND A.SYS_PORT = B.SYS_PORT LEFT OUTER JOIN COMTCCMMNDETAILCODE C ON C.CODE_ID = 'COM046' AND A.MNTRNG_STTUS = C.CODE WHERE 1 = 1
AND to_char(A.CREAT_DT, 'yyyy-MM-dd hh24:mi') BETWEEN #{searchBgnDt} AND #{searchEndDt}
AND A.SYS_NM LIKE '%'||#{searchWrd}||'%'
AND A.SYS_IP LIKE '%'||#{searchWrd}||'%'
AND B.MNGR_NM LIKE '%'||#{searchWrd}||'%'
AND C.CODE_NM LIKE '%'||#{searchWrd}||'%'
ORDER BY A.CREAT_DT DESC
) T0 ) WHERE RNUM BETWEEN #{firstIndex} + 1 AND #{firstIndex} + #{recordCountPerPage}
