mapper.select:
SELECT A.PROCS_ID , A.PROCS_NM , B.CODE_NM PROCS_STTUS , DATE_FORMAT(A.CREAT_DT,'%Y-%m-%d %H:%i:%s') CREAT_DT , A.MNGR_NM , A.MNGR_EMAIL_ADRES FROM COMTNPROCESSMON A LEFT OUTER JOIN COMTCCMMNDETAILCODE B ON B.CODE_ID = 'COM072' AND A.PROCS_STTUS = B.CODE WHERE 1 = 1
AND binary(A.PROCS_NM) LIKE CONCAT('%', #{searchKeyword}, '%')
AND B.CODE_NM = #{searchKeyword}
ORDER BY A.FRST_REGIST_PNTTM DESC LIMIT #{recordCountPerPage} OFFSET #{firstIndex}

SELECT COUNT(A.PROCS_ID) totcnt FROM COMTNPROCESSMON A LEFT OUTER JOIN COMTCCMMNDETAILCODE B ON B.CODE_ID = 'COM072' AND A.PROCS_STTUS = B.CODE WHERE 1 = 1
AND binary(A.PROCS_NM) LIKE CONCAT('%', #{searchKeyword}, '%')
AND B.CODE_NM = #{searchKeyword}

mapper.update:

UPDATE  COMTNPROCESSMON 
   SET  PROCS_NM		  = #{processNm}
   	 ,  MNGR_NM        	  = #{mngrNm}
     ,  MNGR_EMAIL_ADRES   = #{mngrEmailAddr}
     ,  LAST_UPDT_PNTTM = sysdate()
     ,  LAST_UPDUSR_ID    = #{lastUpdusrId}
 WHERE  PROCS_ID        = #{processId}

mapper.insert:

    INSERT 
      INTO  COMTNPROCESSMON
         (  PROCS_ID
         ,  PROCS_NM
         ,  PROCS_STTUS
         ,  CREAT_DT
         ,  MNGR_NM
         ,  MNGR_EMAIL_ADRES
	     ,	FRST_REGISTER_ID
	     ,	FRST_REGIST_PNTTM
         ,  LAST_UPDUSR_ID			     	         
         ,  LAST_UPDT_PNTTM
         ) 
    VALUES  
         (  #{processId}
         ,  #{processNm}
         ,  #{procsSttus}
         ,  #{creatDt}
         ,  #{mngrNm}
         ,  #{mngrEmailAddr}
         ,  #{frstRegisterId}
         ,  sysdate()		         		         
         ,  #{lastUpdusrId}
         ,  sysdate()
         )

mapper.update:

UPDATE  COMTNPROCESSMON 
   SET  PROCS_STTUS       = #{procsSttus}
     ,  CREAT_DT          = #{creatDt}
     ,  LAST_UPDT_PNTTM = sysdate()
     ,  LAST_UPDUSR_ID    = #{lastUpdusrId}
 WHERE  PROCS_NM        = #{processNm}

mapper.select:
SELECT A.LOG_ID , A.PROCS_ID , A.PROCS_NM , C.CODE_NM PROCS_STTUS , DATE_FORMAT(A.CREAT_DT,'%Y-%m-%d %H:%i:%s') CREAT_DT , A.LOG_INFO , A.MNGR_NM , A.MNGR_EMAIL_ADRES FROM COMTNPROCESSMONLOGINFO A, COMTNPROCESSMON B, COMTCCMMNDETAILCODE C WHERE A.PROCS_ID = B.PROCS_ID AND A.PROCS_STTUS = C.CODE AND C.CODE_ID = 'COM072' AND 1 = 1
AND A.CREAT_DT BETWEEN #{searchBgnDt} AND #{searchEndDt}
AND binary(A.PROCS_NM) LIKE CONCAT('%', #{searchKeyword}, '%')
AND C.CODE_NM = #{searchKeyword}
ORDER BY A.FRST_REGIST_PNTTM DESC LIMIT #{recordCountPerPage} OFFSET #{firstIndex}

SELECT COUNT(A.PROCS_ID) totcnt FROM COMTNPROCESSMONLOGINFO A, COMTNPROCESSMON B, COMTCCMMNDETAILCODE C WHERE A.PROCS_ID = B.PROCS_ID AND A.PROCS_STTUS = C.CODE AND C.CODE_ID = 'COM072' AND 1 = 1
AND A.CREAT_DT BETWEEN #{searchBgnDt} AND #{searchEndDt}
AND binary(A.PROCS_NM) LIKE CONCAT('%', #{searchKeyword}, '%')
AND C.CODE_NM = #{searchKeyword}

mapper.insert:

    INSERT 
      INTO  COMTNPROCESSMONLOGINFO
         (  LOG_ID
         ,  PROCS_ID
         ,  PROCS_NM
         ,  PROCS_STTUS
         ,  CREAT_DT
         ,  LOG_INFO
         ,  MNGR_NM
         ,  MNGR_EMAIL_ADRES
	     ,	FRST_REGISTER_ID
	     ,	FRST_REGIST_PNTTM
         ,  LAST_UPDUSR_ID			     	         
         ,  LAST_UPDT_PNTTM
         ) 
    VALUES  
         (  #{logId}
         ,  #{processId}
         ,  #{processNm}
         ,  #{procsSttus}
         ,  #{creatDt}
         ,  #{logInfo}
         ,  #{mngrNm}
         ,  #{mngrEmailAddr}
         ,  #{frstRegisterId}
         ,  sysdate()		         		         
         ,  #{lastUpdusrId}
         ,  sysdate()
         )
