mapper.select:
SELECT A.SERVER_ID, A.SERVER_EQPMN_ID, A.LOG_ID, C.SERVER_EQPMN_NM AS SERVER_NM, C.SERVER_EQPMN_IP, A.CPU_USE_RT, A.MORY_USE_RT, A.SVC_STTUS, (SELECT CODE_NM FROM COMTCCMMNDETAILCODE WHERE CODE_ID = 'COM072' AND CODE = A.SVC_STTUS) AS SVC_STTUS_NM, A.LOG_INFO, C.MNGR_EMAIL_ADRES, DATE_FORMAT(A.CREAT_DT, '%Y-%m-%d %T') AS CREAT_DT, DATE_FORMAT(A.FRST_REGIST_PNTTM, '%Y-%m-%d %T') AS FRST_REGIST_PNTTM, A.FRST_REGISTER_ID, DATE_FORMAT(A.LAST_UPDT_PNTTM, '%Y-%m-%d %T') AS LAST_UPDT_PNTTM, A.LAST_UPDUSR_ID FROM COMTNSERVERRESRCELOGINFO A, COMTNSERVERINFO B, COMTNSERVEREQPMNINFO C WHERE A.SERVER_ID = B.SERVER_ID AND A.SERVER_EQPMN_ID = C.SERVER_EQPMN_ID AND C.SERVER_EQPMN_NM LIKE CONCAT('%' , #{strServerNm}, '%')
AND DATE_FORMAT(A.CREAT_DT, '%Y%m%d') BETWEEN #{strStartDt} AND #{strEndDt}
ORDER BY A.LOG_ID DESC LIMIT #{recordCountPerPage} OFFSET #{firstIndex}
